namespace MultiFlexi\Action;

/**
 * Description of Reschedule.
 *
 * @author vitex
 */
class LaunchJob extends \MultiFlexi\CommonAction
{
    /**
     * Module Caption.
     */
    public static function name(): string
    {
        return _('Launch Job');
    }

    /**
     * Module Description.
     */
    public static function description(): string
    {
        return _('Launch another job');
    }

    public static function logo(): string
    {
        return 'data:image/svg+xml;base64,';
    }

    /**
     * @return \MultiFlexi\Ui\CompanyAppChooser
     */
    public static function inputs(string $prefix)
    {
        return new \MultiFlexi\Ui\CompanyAppChooser($prefix.'[LaunchJob][jobid]', new \MultiFlexi\Company(array_key_exists('cpmpany', $_SESSION) ? $_SESSION['company'] : 0));
    }

    /**
     * Is this Action Suitable for Application.
     *
     * @param Application $app
     */
    public static function usableForApp($app): bool
    {
        return \is_object($app);
    }
}
